import re
import sys
from shutil import get_terminal_size


def clear() -> None:
    sys.stdout.write("\x1b[2J\x1b[H")
    sys.stdout.flush()


def goto(x: int, y: int) -> str:
    return f"\x1b{y};{x}H"  # i still don't know why it's backwards


def get_termcol(rgb: tuple[int, int, int], bg: bool = False) -> str:
    return f"\x1b[48;2;{rgb[0]};{rgb[1]};{rgb[2]}m" if bg else f"\x1b[38;2;{rgb[0]};{rgb[1]};{rgb[2]}m"


def display_width(s: str) -> int:
    # regex by ai
    s = re.sub(r'\x1b\[[0-9;]*m', '', s)
    return len(s)


def text_center(text: str, size: int) -> str:
    padding = size - display_width(text)
    bump = "" if padding % 2 == 0 else " "
    return " " * (padding // 2) + text + " " * (padding // 2) + bump


def screen_center(text: str) -> str:
    x, _ = get_term_size()
    if display_width(text) >= x - 1:
        raise ValueError(f"terminal size is too small! (needed: {x} cols)")
    return text_center(text, x - 1)


def get_term_size() -> tuple[int, int]:
    return get_terminal_size((80, 24))


def prompt(message: str = None, default: str = None, offset: int = 0) -> str:
    if default:
        response = input(" " * offset + f"{message if message else ""} [Default: {default}] >... ").strip()
        return response if response else default
    while True:
        response = input(" " * offset + f"{message} >... ").strip()
        if response:
            return response


def prompt_yn(message: str = None, default: bool = True, offset: int = 0) -> bool:
    df = "Y/n" if default else "y/N"
    response = input(" " * offset + f"{message} [{df}] >... ").strip().lower()
    if not response:
        return default
    return response in ("y", "yes")


def prompt_int(message: str = None, default: int = None, min_val: int = None, max_val: int = None,
               offset: int = 0) -> int:
    while True:
        response = prompt(message, str(default), offset=offset)
        try:
            value = int(response)
            if min_val is not None and value < min_val:
                print()
                continue
            if max_val is not None and value > max_val:
                print()
                continue
            return value
        except ValueError:
            print()


